import React, { Component, Fragment } from 'react';
import {Button, Paper, FormGroup, FormControlLabel, Typography, Switch, FormLabel, FormHelperText, FormControl } from '@material-ui/core';
import styles from './CurrentStatus.css'
import { withStyles } from '@material-ui/core/styles';


class CurrentStatue extends Component {
    state = {
        checkedA: true,
        checkedB: true,
    };


    handleChange = name => event => {
        this.setState({ [name]: event.target.checked });
    };

    render() {
        const { classes } = this.props;
        return (

            <Fragment>
                <Paper className={classes.root} elevation={1}>
                    <Typography variant="h5" component="h3">
                        Current Status
                    </Typography>
                    <Typography component="h6">
                        Friday, 7 December, 12:37:43
                    </Typography>
                    <Typography component="p" className={classes.arduinoId}>
                        Arduino Id : Man Chan
                    </Typography>

                    <FormControl component="fieldset">
                        <FormLabel component="legend">Assign responsibility</FormLabel>
                        <FormGroup>
                            <FormControlLabel control={<Switch value="isGasLit" />} label="Is gas lit" />
                            <FormControlLabel control={<Switch value="isGasLiking" />} label="Is gas liking" />
                        </FormGroup>
                        <FormHelperText>Be careful</FormHelperText>
                    </FormControl>
                </Paper>
            </Fragment>
        );
    }
}

export default  withStyles(styles)(CurrentStatue);
