import React, { Component, Fragment } from 'react';
import {Button, Paper, FormGroup, FormControlLabel, Typography, Switch, FormLabel, FormHelperText, FormControl } from '@material-ui/core';
import styles from './CurrentStatus.css'
import { withStyles } from '@material-ui/core/styles';


class CurrentStatue extends Component {
    state = {
        isGasLit: true,
        isGasLiking: false,
    };


    handleChange = name => event => {
        this.setState({ [name]: event.target.checked });
    };

    render() {
        const { classes } = this.props;
        const {isGasLit, isGasLiking} = this.state;

        return (

            <Fragment>
                <Paper className={classes.root} elevation={1}>
                    <Typography variant="h5" component="h3">
                        Current Status
                    </Typography>
                    <Typography component="h6">
                        Friday, 7 December, 12:37:43
                    </Typography>
                    <Typography component="p" className={classes.arduinoId}>
                        Arduino Id : BlaBlaBla
                    </Typography>



                    <div className={classes.isGasLitClass}>
                    <Typography>
                        Gas is lit :
                    </Typography>

                    {isGasLit ?

                            <Fragment>
                                <Typography>
                                    Gas Is lit!
                                </Typography>
                            </Fragment>:

                            <Fragment>
                                <Typography>
                                    Gas isn't lit
                                </Typography>
                            </Fragment>
                    }
                    </div>

                    <div className={classes.isGasLitClass}>
                        <Typography>
                            Gas is liking :
                        </Typography>

                        {isGasLit ?
                            <Fragment>
                                <Typography>
                                    Gas Is liking!
                                </Typography>
                            </Fragment>:

                            <Fragment>
                                <Typography>
                                    Gas isn't liking
                                </Typography>
                            </Fragment>
                        }
                    </div>


                </Paper>
            </Fragment>


        );
    }
}

export default  withStyles(styles)(CurrentStatue);
